CREATE TABLE GENERICSETTINGS(
  ID INTEGER NOT NULL,
  TIGERVERSION  VARCHAR(4),
  USGSVERSION   VARCHAR(8),
  PRIMARY KEY(ID)
);


CREATE TABLE SHAPEFILEMETADATA(
    ID  INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),
    GEOID   VARCHAR(20),
    FILENAME  VARCHAR(255),
    VERSION  VARCHAR(4),
    STATUS VARCHAR(16),
    MTFCCCODE VARCHAR(6),
    PRIMARY KEY(ID)
);

create index idx_sm_geoid on SHAPEFILEMETADATA(GEOID);
create index idx_sm_mtfcccode on SHAPEFILEMETADATA(MTFCCCODE);

CREATE TABLE FEATURESMETADATA(
    ID INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),
    STATE VARCHAR(30),
    STATEABBR VARCHAR(2),
    USGSDATE VARCHAR(8),
    FILENAME  VARCHAR(255),
    STATEGEOID  VARCHAR(2),
    STATUS  VARCHAR(15),
    PRIMARY KEY(ID)
);

create index idx_fm_stategeoid on FEATURESMETADATA(STATEGEOID);
create index idx_fm_status on FEATURESMETADATA(STATUS);

CREATE TABLE FEATURE(
    FEATUREID   INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),
    FEATURENAME        VARCHAR(100),
    FEATURECLASS  VARCHAR(100),
    LAT           NUMERIC(14,5),
    LNG           NUMERIC(14,5),
    FEATURESOURCE  VARCHAR(10),
    FEATUREMETADATAID INTEGER,
    PRIMARY KEY(FEATUREID),
    FOREIGN KEY(FEATUREMETADATAID) REFERENCES FEATURESMETADATA(ID)
);

create index idx_featureclass on FEATURE(FEATURECLASS);
create index idx_f_fmid on FEATURE(FEATUREMETADATAID);

CREATE TABLE MTFCC(
        MTFCCID       INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),,
        MTFCCCODE     CHAR(5),
        FEATURECLASS  VARCHAR(100),
        SUPERCLASS    VARCHAR(75),
        POINT         BOOL,
        LINEAR1        BOOL,
        AREAL         BOOL,
        FEATURECLASSDESCRIPTION TEXT,
        PRIMARY KEY(MTFCCID)
);

create index idx_mtfcc_code ON MTFCC(MTFCCCODE);

CREATE TABLE LOCATION(
        LOCATIONID INTEGER NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),
        GEOID VARCHAR(20),
        STATEFP VARCHAR(2),
        COUNTYFP VARCHAR(3),
        LOCATIONNAME VARCHAR(100),
        LSAD VARCHAR(2),
        MTFCCID INTEGER,
        SHAPEFILEMETADATAID INTEGER,
        URBANRURAL CHAR(1),
        FUNCTIONALSTATUS CHAR(1),
        LANDAREA BIGINT,
        WATERAREA BIGINT,
        INTERNALLAT NUMERIC(14,5),
        INTERNALLNG NUMERIC(14,5),
        TIGERVERSION VARCHAR(8),
        MINLAT NUMERIC(14,5),
        MINLNG NUMERIC(14,5),
        MAXLAT NUMERIC(14,5),
        MAXLNG NUMERIC(14,5),
        PRIMARY KEY(LOCATIONID),
        FOREIGN KEY(MTFCCID) REFERENCES MTFCC(MTFCCID)
          ON DELETE CASCADE
          ON UPDATE CASCADE,
        FOREIGN KEY(SHAPEFILEMETADATAID) REFERENCES SHAPEFILEMETADATA(ID)
          ON UPDATE CASCADE
          ON DELETE SET NULL
);

create index idx_l_smid on LOCATION(SHAPEFILEMETADATAID);
CREATE INDEX idx_l_mtfccid on LOCATION(MTFCCID);
create index idx_l_geoid on LOCATION(GEOID);
create index idx_l_statefp on LOCATION(STATEFP);
create index idx_l_countyfp on LOCATION(COUNTYFP);

CREATE TABLE BORDERPOINT(
        BORDERID    BIGINT NOT NULL GENERATED BY DEFAULT AS IDENTITY(START WITH 1000),
        LOCATIONID  INTEGER,
        LAT         NUMERIC(14,5),
        LNG         NUMERIC(14,5),
        PRIMARY KEY(BORDERID),
        FOREIGN KEY(LOCATIONID) REFERENCES LOCATION(LOCATIONID)
);

create index idx_bp_l on BORDERPOINT(LOCATIONID);

